import math

def analyze_and_transform(numbers):
    if not numbers:
        print("The list is empty")
        return
    
    print(f"Total numbers: {len(numbers)}")

    average = sum(numbers) / len(numbers)
    print(f"Average: {average:0.2f}")

    # over, under = 0, 0
    # for num in numbers:
    #     if num > average:
    #         over += 1
    #     else:
    #         under += 1
    over = sum(1 for num in numbers if num > average)
    under = sum(1 for num in numbers if num < average)
    print(f"Numbers over the average: {over}")
    print(f"Numbers under the average: {under}")

    summed = 0
    for num in numbers:
        summed += (num - average) ** 2
    variance = summed/len(numbers)
    print(f"Variance: {variance:0.2f}")

    variance_2 = sum((num - average) ** 2 for num in numbers) / len(numbers)
    print(f"Variance 2: {variance_2:0.2f}")

    std_dev = variance ** 0.5
    std_dev_2 = math.sqrt(variance)
    print(f"Standared Deviation: {std_dev:0.2f}")
    print(f"Standard Deviation 2: {std_dev_2:0.2f}")

    positive_numbers = [num for num in numbers if num >= 0]
    avg_positive = sum(positive_numbers) / len(positive_numbers)
    print(f"Average of non-negative numbers: {avg_positive:0.2f}")

    negative_numbers = [num for num in numbers if num < 0]
    avg_negative = sum(negative_numbers) / len(negative_numbers)
    print(f"Average of negative numbers: {avg_negative:0.2f}")

    min_num = min(numbers)
    max_num = max(numbers)
    normalized_data = [(num - min_num) / (max_num - min_num) for num in numbers]
    print(f"Normalized Data: {normalized_data}")

    avg_normalized = sum(normalized_data) / len(normalized_data)
    print(f"Average of normalized Data: {avg_normalized:0.2f}")

